mapper.select:
select employeeId, first_name, last_name, email, hiredate hireDate
  from emp
 where employeeId = #{employeeId};

mapper.update:
update emp
   set first_name = #{firstName}
      ,last_name = #{lastName}
      ,email = #{email}
      ,hiredate = #{hireDate}
 where employeeId = #{employeeId}
